package Loops;

public class If_Else {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
        int i = 51;
       
        if(i%2==0)
        {
        	System.out.println("Number is even no.");
        }
        else
        {
        	System.out.println("Number is odd no.");
        }
	}

}
