package Variable;

public class Localvariableexample {

	public void myAge()
	{
		int age=0;
		age=age+26;
		System.out.println("My age is "+age);
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		Localvariableexample test=new Localvariableexample();
		test.myAge();

	}

}
